import React, { useState } from "react";
import MainHeader from "./components/Layout/MainHeader";
import TaskBar from "./components/Layout/TaskBar";
import VehicleList from "./components/Vehicles/VehicleList";
import VehicleForm from "./components/Form/VehicleForm";
import TollForm from "./components/Form/TollForm";

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [isTollOpen, setIsTollOpen] = useState(false);
  const [tollItem, setTollItem] = useState([
    {
      name: "Chengalpattu",
      vehicle: "30",
      lcv: "50",
      truck: "100",
      hvehicle: "160",
    },
    {
      name: "Kappalur",
      vehicle: "50",
      lcv: "80",
      truck: "120",
      hvehicle: "200",
    },
    {
      name: "Krishnagiri",
      vehicle: "40",
      lcv: "60",
      truck: "130",
      hvehicle: "200",
    }
  ]);

  const handleVehicleEntry = () => {
    setIsOpen(true);
  };

  const handleCloseModal = () => {
    setIsOpen(false);
  };

  const handleTollName = () => {
    setIsTollOpen(true);
  };

  const handleCloseTollModal = () => {
    setIsTollOpen(false);
  };

  const handleTollItem = (form) => {
    setTollItem([{ name: form.name, vehicle: form.vehicle }, ...tollItem]);
  };

  return (
    <div className="container">
      {isOpen && <VehicleForm onClick={handleCloseModal} />}
      {isTollOpen && (
        <TollForm onClick={handleCloseTollModal} tollItem={handleTollItem} />
      )}
      <MainHeader />
      <TaskBar
        clickVehicleEntry={handleVehicleEntry}
        clickTollName={handleTollName}
      />
      <VehicleList />
      {tollItem.map((item) => {
        return <div>{item.name}</div>;
      })}
    </div>
  );
}

export default App;
