import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialTollFormState = {
  name: "",
  vehicle: "",
  lcv: "",
  truck: "",
  hvehicle: "",
};

const tollSlice = createSlice({
  name: "toll",
  initialState: initialTollFormState,
  reducers: {
    setToll(state, action) {
      state.name = action.payload?.name;
      state.vehicle = action.payload?.vehicle;
      state.lcv = action.payload?.lcv;
      state.truck = action.payload?.truck;
      state.hvehicle = action.payload?.hvehicle;
    },
    // updateName(state, action) {
