import { useDispatch, useSelector } from "react-redux";
import Modal from "../UI/Modal";
import Button from "../UI/Button";
import "./VehicleForm.css";
import { tollActions } from "../../store/index";

export default function TollForm(props) {
  const dispatch = useDispatch();
  const form = useSelector((state) => state.toll);

  const handleNameChange = (event) => {
    dispatch(tollActions.updateName(event.target.value));
  };

  const handleVehicleChange = (event) => {
    dispatch(tollActions.updateVehicle(event.target.value));
  };

  const handleLCVChange = (event) => {
    dispatch(tollActions.updateLCV(event.target.value));
  };

  const handleTruckChange = (event) => {
    dispatch(tollActions.updateTruck(event.target.value));
  };

  const handleHVehicleChange = (event) => {
    dispatch(tollActions.updateHvehicle(event.target.value));
  };

  const handleTollSubmit = (event) => {
    event.preventDefault();
    dispatch(tollActions.clearToll());
  };

  return (
    <Modal onClose={props.onClick}>
      <div className="vehicle-form">
        <h2>Add new toll</h2>
        <form onSubmit={handleTollSubmit}>
          <label>Toll Name*</label>
          <input type="text" onChange={handleNameChange} />

          <label>Vehicle Fare details*</label>

          <label>
            Car/Jeep/Van:
            <input type="number" onChange={handleVehicleChange} />
          </label>

          <label>
            LCV:
            <input type="number" onChange={handleLCVChange} />
          </label>

          <label>
            Truck/Bus:
            <input type="number" onChange={handleTruckChange} />
          </label>

          <label>
            Heavy vehicle:
            <input type="number" onChange={handleHVehicleChange} />
          </label>

          <Button className="submit-button" type="submit">
            Add details
          </Button>
        </form>
      </div>
    </Modal>
  );
}
