import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialTollFormState = {
  name: "",
  vehicle: "",
  lcv: "",
  truck: "",
  hvehicle: "",
};

const tollSlice = createSlice({
  name: "toll",
  initialState: initialTollFormState,
  reducers: {
    updateName(state, action) {
      state.name = action.payload;
    },
    updateVehicle(state, action) {
      state.vehicle = action.payload;
    },
    updateLCV(state, action) {
      state.lcv = action.payload;
    },
    updateTruck(state, action) {
      state.truck = action.payload;
    },
    updateHvehicle(state, action) {
      state.hvehicle = action.payload;
    },
    clearToll(state) {
      state.name = "";
      state.vehicle = "";
      state.lcv = "";
      state.truck = "";
      state.hvehicle = "";
    },
  },
});

const store = configureStore({
  reducer: {toll: tollSlice.reducer},
});

export const tollActions = tollSlice.actions;
export default store;
