const modalstyles = {
  position: "fixed",
  left: "50%",
  top: "50%",
  backgroundColor: "red",
  zIndex: 1000,
  padding: "50px"
};

export default function Modal(props) {
  return (
    <>
      <div className="backdrop"></div>
      <div style={modalstyles} onClick={props.onClose}>
        {props.children}
      </div>
    </>
  );
}
