import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Modal from "../UI/Modal";
import Button from "../UI/Button";
import "./VehicleForm.css";
import { vehicleActions } from "../../store/index";

export default function VehicleForm(props) {
  const dispatch = useDispatch();
  const [selectVehicle, setSelectedVehicle] = useState("");
  const [selectedValue, setSelectedValue] = useState("");

  const handleSelectChange = (e) => {
    setSelectedValue(e.target.value);
    // console.log(e.target.value);
  };

  const handleVehicleChange = (e) => {
    setSelectedVehicle(e.target.value);
    // console.log(e.target.value);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    dispatch(tollActions.updateVehicle({ name, value }));
  }

  function getIndex(name) {
    return props.items.findIndex((obj) => obj.name === name);
  }
  const indexValue = getIndex(selectedValue);

  let currentVehicle;

  if (selectVehicle === "CAR/JEEP/VAN") {
    currentVehicle = props.items[indexValue].vehicle;
  }
  if (selectVehicle === "LCV") {
    currentVehicle = props.items[indexValue].lcv;
  }
  if (selectVehicle === "TRUCK/BUS") {
    currentVehicle = props.items[indexValue].truck;
  }
  if (selectVehicle === "HEAVYVEHICLE") {
    currentVehicle = props.items[indexValue].hvehicle;
  }

  return (
    <Modal onClose={props.onClick}>
      <div className="vehicle-form">
        <h2>Add new entry</h2>
        <form>
          <label htmlFor="select-toll">Select Toll*</label>
          <select
            id="select-toll"
            name="toll"
            value={selectedValue}
            onChange={handleChange}
          >
            {props.items.map((item) => {
              return <option value={item.name}>{item.name}</option>;
            })}
          </select>

          <label htmlFor="vehicletype">Select vehicle type*</label>
          <select
            id="vehicletype"
            name="vehicletype"
            value={selectVehicle}
            onChange={handleChange}
          >
            <option value="CAR/JEEP/VAN">CAR/JEEP/VAN</option>
            <option value="LCV">LCV</option>
            <option value="TRUCK/BUS">TRUCK/BUS</option>
            <option value="HEAVYVEHICLE">HEAVY VEHICLE</option>
          </select>

          <label>Vehicle Number*</label>
          <input type="text" name="vehiclenumber" onChange={handleChange}/>

          <label>Tariff*</label>
          <input type="text" readOnly name="tariff" value={currentVehicle} />

          <Button className="submit-button" type="submit">
            Add Entry
          </Button>
        </form>
      </div>
    </Modal>
  );
}
