import { useDispatch } from "react-redux";
import Modal from "../UI/Modal";
import Button from "../UI/Button";
import "./VehicleForm.css";
import { tollActions } from "../../store/index";

export default function TollForm(props) {
  const dispatch = useDispatch();

  const handleNameChange = (event) => {
    dispatch(tollActions.updateName(event.target.value));
  }

  const handleVehicleChange = (event) => {
    dispatch(tollActions.updateVehicle(event.target.value));
    console.log(event.target.value)
  }

  return (
    <Modal onClose={props.onClick}>
      <div className="vehicle-form">
        <h2>Add new toll</h2>
        <form>
          <label>Toll Name*</label>
          <input
            type="text"
            onChange={handleNameChange}
          />

          <label>Vehicle Fare details*</label>

          <label>
            Car/Jeep/Van:
            <input
              type="number"
              onChange={handleVehicleChange}
            />
          </label>

          <label>
            LCV:
            <input
              type="number"
            />
          </label>

          <label>
            Truck/Bus:
            <input
              type="number"
            />
          </label>

          <label>
            Heavy vehicle:
            <input
              type="number"
            />
          </label>

          <Button className="submit-button" type="submit">
            Add details
          </Button>
        </form>
      </div>
    </Modal>
  );
}
