import React, { useState } from "react";
import Modal from "../UI/Modal";
import Button from "../UI/Button";
import "./VehicleForm.css";

export default function VehicleForm(props) {
  const [selectVehicle, setSelectedVehicle] = useState('');
  const [selectedValue, setSelectedValue] = useState('');

  const handleSelectChange = (e) => {
    setSelectedValue(e.target.value);
    // console.log(e.target.value);
  }

  const handleVehicleChange = (e) => {
    setSelectedVehicle(e.target.value);
    // console.log(e.target.value);
  } 

  function getIndex(name) {
    return props.items.findIndex(obj => obj.name === name);
  }

  const indexValue = getIndex(selectedValue)

  if(selectedValue === indexValue){
    console.log(props.items[0].truck);
  } else {
    console.log(props.items[indexValue].truck);
  }
  
  

  return (
    <Modal onClose={props.onClick}>
      <div className="vehicle-form">
        <h2>Add new entry</h2>
        <form>
          <label htmlFor="vehicletype">Select vehicle type*</label>
          <select id="vehicletype" value={selectVehicle} onChange={handleVehicleChange}>
            <option value="CAR/JEEP/VAN">CAR/JEEP/VAN</option>
            <option value="LCV">LCV</option>
            <option value="TRUCK/BUS">TRUCK/BUS</option>
            <option value="HEAVYVEHICLE">HEAVY VEHICLE</option>
          </select>

          <label>Vehicle Number*</label>
          <input type="text" />

          <label htmlFor="select-toll">Select Toll*</label>
          <select id="select-toll" value={selectedValue} onChange={handleSelectChange}>
            {props.items.map((item) => {
              return <option value={item.name}>{item.name}</option>;
            })}
          </select>

          <label>Tariff*</label>
          <input type="text" readOnly value="100" />

          <Button className="submit-button">Add Entry</Button>
        </form>
      </div>
    </Modal>
  );
}
