import React, { useState } from "react";
import { useDispatch } from "react-redux";
import Modal from "../UI/Modal";
import Button from "../UI/Button";
import "./VehicleForm.css";

import { tollActions } from "../../store/index";

export default function TollForm(props) {
  const [formValues, setFormValues] = useState(tollActions.initialState);
  const dispatch = useDispatch();

  console.log(formValues)

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(tollActions.clearForm());
    // Handle submission logic here
  };

  return (
    <Modal onClose={props.onClick}>
      <div className="vehicle-form">
        <h2>Add new toll</h2>
        <form onSubmit={handleSubmit}>
          <label>Toll Name*</label>
          <input
            type="text"
            onChange={handleInputChange}
            name="name"
            value={formValues.name}
          />

          <label>Vehicle Fare details*</label>

          <label>
            Car/Jeep/Van:
            <input
              type="number"
              onChange={handleInputChange}
              name="vehicle"
              value={formValues.vehicle}
            />
          </label>

          <label>
            LCV:
            <input
              type="number"
              onChange={handleInputChange}
              name="lcv"
              value={formValues.lcv}
            />
          </label>

          <label>
            Truck/Bus:
            <input
              type="number"
              onChange={handleInputChange}
              name="truck"
              value={formValues.truck}
            />
          </label>

          <label>
            Heavy vehicle:
            <input
              type="number"
              onChange={handleInputChange}
              name="hvehicle"
              value={formValues.hvehicle}
            />
          </label>

          <Button className="submit-button" type="submit">
            Add details
          </Button>
        </form>
      </div>
    </Modal>
  );
}
