import React, { useState } from "react";
import { useDispatch } from "react-redux";
import Modal from "../UI/Modal";
import Button from "../UI/Button";
import "./VehicleForm.css";

export default function TollForm(props) {
  const [formValues, setFormValues] = useState();
  const dispatch = useDispatch();

  return (
    <Modal onClose={props.onClick}>
      <div className="vehicle-form">
        <h2>Add new toll</h2>
        <form>
          <label>Toll Name*</label>
          <input type="text" onChange={handleInputChange} name="name" />

          <label>Vehicle Fare details*</label>

          <label>
            Car/Jeep/Van:
            <input type="number" onChange={handleInputChange} name="vehicle" />
          </label>

          <label>
            LCV:
            <input type="number" onChange={handleInputChange} name="lcv" />
          </label>

          <label>
            Truck/Bus:
            <input type="number" onChange={handleInputChange} name="truck" />
          </label>

          <label>
            Heavy vehicle:
            <input type="number" onChange={handleInputChange} name="hvehicle" />
          </label>

          <Button className="submit-button" type="submit">
            Add details
          </Button>
        </form>
      </div>
    </Modal>
  );
}
